<?php

namespace BasicDashboard\Web\Subcategories\Controllers;

use BasicDashboard\Web\Common\BaseController;
use BasicDashboard\Web\Subcategories\Services\SubcategoryService;
use BasicDashboard\Web\Subcategories\Validation\DeleteSubcategoryRequest;
use BasicDashboard\Web\Subcategories\Validation\StoreSubcategoryRequest;
use BasicDashboard\Web\Subcategories\Validation\UpdateSubcategoryRequest;
use Illuminate\Http\RedirectResponse;
use Illuminate\Http\Request;
use Illuminate\View\View;

/**
 *
 * A SubcategoryController is responsible for receive request and return response.
